import React, { useEffect, useState } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Card from '@material-ui/core/Card'
import CardActions from '@material-ui/core/CardActions'
import CardContent from '@material-ui/core/CardContent'
import Button from '@material-ui/core/Button'
import Typography from '@material-ui/core/Typography'
import Link from '@material-ui/core/Link'

import StraightAnglePieChart from './StraightAnglePieChart'

import { getSentiment } from '../services/sentiment'

const useStyles = makeStyles({
    root: {
        minWidth: 275,
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'space-evenly',
        width: '80%',
        paddingTop: '21px',
        alignItems: 'center',
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: 14,
    },
    pos: {
        marginBottom: 12,
    },
    stock: {
        marginBottom: 12,
    },
})

export default function SimpleCard(props) {
    const classes = useStyles()
    let keys = []
    let values = []
    const [sentiment, setSentiment] = useState([])
    useEffect(() => {
        let mounted = true
        getSentiment(props.stock).then((sentiment) => {
            if (mounted) {
                keys = Object.keys(sentiment)
                values = Object.values(sentiment)
                sentiment = keys.map((x, i) => {
                    return { name: x, value: parseFloat(values[i].toFixed(2)) }
                })
                setSentiment(sentiment)
            }
        })
        return () => (mounted = false)
    }, [])

    return (
        <Card className={classes.root}>
            <CardContent className={classes.stock}>
                <Typography
                    className={classes.title}
                    color="textSecondary"
                    gutterBottom
                >
                    Stock of the Day
                </Typography>
                <Typography variant="h5" component="h2">
                    {props.stock}
                </Typography>
                <Typography className={classes.pos} color="textSecondary">
                    {props.stockName}
                </Typography>
                <CardActions>
                    <Link href={`/stock/${props.stock}`}>
                        <Button size="small">
                            Learn More about {props.stock}
                        </Button>
                    </Link>
                </CardActions>
            </CardContent>
            <CardContent>
                <Typography
                    className={classes.title}
                    color="textSecondary"
                    gutterBottom
                >
                    Sentiment
                </Typography>
            </CardContent>
            <StraightAnglePieChart data={sentiment} />
        </Card>
    )
}
